create table Students (
	RollNo INT,
	Name VARCHAR(50),
	EmailId VARCHAR(50),
	PrimaryPhone VARCHAR(50),
	SecondaryPhone VARCHAR(50),
	Gender VARCHAR(50),
	ResidentialAddress VARCHAR(50),
	About VARCHAR(50),
	ProfilePicture VARCHAR(50),
    PRIMARY KEY (RollNo)
);

/* Mock enteries for 30 students*/



CREATE TABLE Courses (
    Id int NOT NULL AUTO_INCREMENT,
    Name VARCHAR(120),
    PRIMARY KEY (Id),
    TotalHours int,
    Thumbnail VARCHAR(300)
);


/* Mock enteries for 10 courses*/


CREATE TABLE StudentCourseMapping (
    Id int NOT NULL AUTO_INCREMENT,
    CourseId int,
    RollNo int,
    PRIMARY KEY (Id),
    FOREIGN KEY (CourseId) REFERENCES Courses(Id),
    FOREIGN KEY (RollNo) REFERENCES Students(RollNo)
);





CREATE TABLE Users (
    Id int NOT NULL AUTO_INCREMENT,
    Name VARCHAR(120),
    EmailId VARCHAR(120),
    AccessToken VARCHAR(320),
    PRIMARY KEY (Id),
    Password VARCHAR(300)
);


